import React, { Component } from 'react';

// Button class component accepting props from our Fruit component.App.
// Here we set up state as the name of the button.
// We set up binding for our click handler.
export class DeleteButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fruitData: this.props.fruitData
    };

    this.handleClick = this.handleClick.bind(this);
  }

  // The click handler calls our random number generator which will give us
  // our random colour to change the state with setState.
  handleClick() {
    this.setState(() => ({
      fruitData: this.state.fruitData.filter(
        fruit => fruit.fruit !== this.props.fruit
      )
    }));
    console.log(this.state.fruitData);
  }

  render() {
    return <button onClick={this.handleClick}>{this.props.name}</button>;
  }
}
