import React from 'react';
import { Button } from './Button';
import '../css/fruit.css';

// Fruit Functional component passing props down to our button.
export const Fruit = props => {
  return (
    <>
      <div className="fruitLine">
        <p>{props.fruit}</p>
        <Button name="Change Colour" colour={props.colour} />
        <Button name="Delete fruit" />
      </div>
    </>
  );
};
