import React from 'react';
import { Button } from './Button';
import '../css/fruit.css';

export const Fruit = props => {
  return (
    <>
      <div className="fruitLine">
        <p>{props.fruit}</p>
        <Button
          style={{ backgroundColor: props.colour }}
          name="Change Colour"
        />
      </div>
    </>
  );
};
