import React from 'react'



export class Button extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            name: this.props.name,
            colour: this.props.colour
        }
    }
    render() {
        return <button style={{backgroundColor: this.state.colour}}>{this.state.name}</button>
    }

}
