import React, { Component } from 'react';
import ReactDom from 'react-dom';
import { Header } from './components/Header';
import { Fruit } from './components/Fruit';
import { fruitData } from './fruitData';

// Main App. Get individual fruits from our fruitData array and pass down as props to
// our Fruit component.
class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fruitData: fruitData
    };
  }

  //Delete Button
  handleClick = id => {
    this.setState(() => ({
      fruitData: this.state.fruitData.filter(fruit => fruit.id === id)
    }));
  };

  render() {
    const fruitItems = this.state.fruitData.map(fruit => (
      <Fruit
        colour={fruit.colour}
        key={fruit.id}
        fruit={fruit.fruit}
        id={fruit.id}
        handleClick={this.handleClick(this.props.id)}
      />
    ));
    return (
      <>
        <Header />
        {fruitItems}
      </>
    );
  }
}

ReactDom.render(<App />, document.querySelector('#root'));
